mapper.select:
/*이용 내역 총 튜플 수*/
SELECT
	COUNT(*)
FROM
	goods_management;

mapper.update:
UPDATE goods_management

		seat_cd		= #{seatCd, jdbcType=VARCHAR}
	
		,leave_time			= #{leaveTime}
	
	
		,use_time			= #{useTime}
	
	
		,remaining_time			= #{remainingTime}
	

WHERE
	goods_management_cd = #{goodsManagementCd};

mapper.insert:

	SELECT sf_new_goods_management_cd()

/*이용권 사용*/
INSERT INTO goods_management
	(goods_management_cd
	,user_id
	,order_cd
	,goods_cd
	,seat_cd
	,admission_time
	,leave_time
	,use_time
	,remaining_time
	,order_expiration_date)
	VALUES (
		sf_new_goods_management_cd()
		,#{userId}
		,#{orderCd}
		,#{goodsCd}
		,#{seatCd}
		,#{admissionTime}
		,#{leaveTime}
		,#{useTime}
		,#{remainingTime}
		,#{orderExpirationDate});
