mapper.update:
/*이용권수정*/ UPDATE goods
goods_ctg_cd = #{goodsCtgCd},
goods_nm = #{goodsNm},
goods_time_con = #{goodsTimeCon},
price = #{price},
goods_expiration_date = #{goodsExpirationDate}

mapper.insert:

	SELECT sf_new_goods_cd()

/*이용권 등록*/
INSERT INTO goods
	(goods_cd
	,goods_ctg_cd
	,goods_nm
	,goods_time_con
	,price
	,goods_expiration_date
	,goods_insert_date)
	VALUES (
		sf_new_goods_cd()
		,#{goodsCtgCd}
		,#{goodsNm}
		,#{goodsTimeCon}
		,#{price}
		,#{goodsExpirationDate}
		,NOW());
