mapper.update:
	UPDATE goods_order
	set give_time = #{giveTime}
	WHERE
		order_cd = #{orderCd};

mapper.insert:

	SELECT sf_new_order_cd()

/*이용권 구매*/
INSERT INTO goods_order
	(order_cd
	,user_id
	,goods_cd
	,give_time
	,price
	,order_insert_date
	,order_expiration_date
	,goods_ctg_cd)
	VALUES (
		sf_new_order_cd()
		,#{userId}
		,#{goodsCd}
		,#{giveTime}
		,#{price}
		,#{orderInsertDate}
		,#{orderExpirationDate}
		,#{goodsCtgCd});
