mapper.insert:

	SELECT sf_new_order_cd()

/*이용권 구매*/
INSERT INTO goods_order
	(order_cd
	,user_id
	,goods_cd
	,remaining_time
	,price
	,order_insert_date
	,order_expiration_date)
	VALUES (
		sf_new_order_cd()
		,#{userId}
		,#{goodsCd}
		,#{remainingTime}
		,#{price}
		,#{orderInsertDate}
		,#{orderExpirationDate});
